import React, { useState } from 'react';
import '../styles/admin_home.css';
import logo from '../assets/images/logo-zeduc.png'
import { House, AlarmClockCheck, MessagesSquare, TrendingUp, UtensilsCrossed, Settings, UserCog, Search, Bell, ChevronDown, Check, X } from "lucide-react";

function AdminHome() {
  const [activeFilters, setActiveFilters] = useState(['#215', '#216', '#217', '#218', '#219', '#220']);
  const [activePage, setActivePage] = useState('HOME');

  const orderData = [
    {
      id: '#215',
      date: '22 Octobre 2025, 12:21 PM',
      userInitial: 'J',
      userColor: 'red',
      items: [
        { 
          name: 'Okok sucré', 
          description: 'Un okok préparé avec soin...',
          price: '1000F',
          qty: 3,
          image: 'https://recettesdafrique.com/wp-content/uploads/cooked/images/recipes/recipe_5005.jpg'
        },
        { 
          name: 'Eru', 
          description: 'Un plat traditionnel riche en saveurs...',
          price: '1000F',
          qty: 1,
          image: 'data:image/jpeg;base64,'
        }
      ],
      totalItems: 4,
      total: '4000F',
      status: 'pending'
    },
    {
      id: '#216',
      date: '23 Octobre 2025, 12:23 PM',
      userInitial: 'M',
      userColor: 'grey',
      items: [
        { 
          name: 'Okok sucré', 
          description: 'Un okok préparé avec soin ...',
          price: '1000F',
          qty: 1,
          image: 'https://recettesdafrique.com/wp-content/uploads/cooked/images/recipes/recipe_5005.jpg'
        },
        { 
          name: 'Poulet pané', 
          description: 'Un poulet très savoureux...',
          price: '1000F',
          qty: 1,
          image: 'data:image/jpeg;base64,'
        }
      ],
      totalItems: 2,
      total: '2000F',
      status: 'preparing'
    },
    {
      id: '#216',
      date: '23 Octobre 2025, 12:23 PM',
      userInitial: 'M',
      userColor: 'grey',
      items: [
        { 
          name: 'Okok sucré', 
          description: 'Un okok préparé avec soin ...',
          price: '1000F',
          qty: 1,
          image: 'https://recettesdafrique.com/wp-content/uploads/cooked/images/recipes/recipe_5005.jpg'
        },
        { 
          name: 'Poulet pané', 
          description: 'Un poulet très savoureux...',
          price: '1000F',
          qty: 1,
          image: 'data:image/jpeg;base64,'
        }
      ],
      totalItems: 2,
      total: '2000F',
      status: 'preparing'
    },
    {
      id: '#218',
      date: '22 Mai 2021, 12:21 PM',
      userInitial: 'A',
      userColor: 'green',
      items: [],
      totalItems: 0,
      total: '$0',
      status: 'cancelled'
    },
    {
      id: '#219',
      date: '22 Mai 2021, 12:21 PM',
      userInitial: 'M',
      userColor: 'grey',
      items: [],
      totalItems: 0,
      total: '$0',
      status: 'cancelled'
    },
    {
      id: '#220',
      date: '22 Mai 2021, 12:21 PM',
      userInitial: 'M',
      userColor: 'grey',
      items: [],
      totalItems: 0,
      total: '$0',
      status: 'cancelled'
    }
  ];

  const toggleFilter = (orderId) => {
    setActiveFilters(prev => 
      prev.includes(orderId) 
        ? prev.filter(id => id !== orderId)
        : [...prev, orderId]
    );
  };

  const filteredOrders = orderData.filter(order => activeFilters.includes(order.id));

  return (
    <>
      <div className="sidebar">
        <div class="logo-container">
            <img src={logo} alt="Zeduc Space Logo" class="logo"/>
        </div>
        
        <a 
  href="/admin" 
  className={`nav-link ${activePage === 'HOME' ? 'active' : ''}`}
  onClick={() => setActivePage('HOME')}
>
  <House size={18} color={activePage === 'HOME' ? '#D4A574' : '#ffffff'} />
  HOME
</a>

<a 
  href="/order_history" 
  className={`nav-link ${activePage === 'ORDER HISTORY' ? 'active' : ''}`}
  onClick={() => setActivePage('ORDER HISTORY')}
>
  <AlarmClockCheck size={18} color={activePage === 'ORDER HISTORY' ? '#D4A574' : '#ffffff'} />
  ORDER HISTORY
</a>

<a 
  href="/messages_admin" 
  className={`nav-link ${activePage === 'MESSAGES' ? 'active' : ''}`}
  onClick={() => setActivePage('MESSAGES')}
>
  <MessagesSquare size={18} color={activePage === 'MESSAGES' ? '#D4A574' : '#ffffff'} />
  MESSAGES
</a>

<a 
  href="/admin_statistics" 
  className={`nav-link ${activePage === 'STATISTICS' ? 'active' : ''}`}
  onClick={() => setActivePage('STATISTICS')}
>
  <TrendingUp size={18} color={activePage === 'STATISTICS' ? '#D4A574' : '#ffffff'} />
  STATISTICS
</a>
        
        <a 
          href="/products" 
          className={`nav-link ${activePage === 'PRODUCTS' ? 'active' : ''}`}
          onClick={() => setActivePage('PRODUCTS')}
        >
          <UtensilsCrossed size={18} color={activePage === 'PRODUCTS' ? '#D4A574' : '#ffffff'} />
          PRODUCTS
        </a>
        
        <a 
          href="/settings" 
          className={`nav-link ${activePage === 'SETTINGS' ? 'active' : ''}`}
          onClick={() => setActivePage('SETTINGS')}
        >
          <Settings size={18} color={activePage === 'SETTINGS' ? '#D4A574' : '#ffffff'} />
          SETTINGS
        </a>
        
        <a 
          href="/account_management" 
          className={`nav-link ${activePage === 'ACCOUNT MANAGEMENT' ? 'active' : ''}`}
          onClick={() => setActivePage('ACCOUNT MANAGEMENT')}
        >
          <UserCog size={18} color={activePage === 'ACCOUNT MANAGEMENT' ? '#D4A574' : '#ffffff'} />
          ACCOUNT MANAGEMENT
        </a>

        <div className="restaurant-status">
          <div className="status-dot"></div>
          <span>Restaurant Open</span>
        </div>
      </div>

      <div className="content">
        <div className="header">
          <div className="search-wrapper">
            <input type="text" placeholder="Search"/>
            <Search className="search-icon" size={18} />
          </div>
          <div className="user-info">
            <span>Admin1_resto</span>
            <div className="user-avatar">A</div>
            <ChevronDown className="chevron-down" size={20} />
            <Bell className="notification-bell" size={20} />
          </div>
        </div>

        <div className="main-content">
          <h2>Liste des commandes</h2>
          
          {/* Filter Tags */}
          <div className="filter-tags">
            {orderData.map(order => {
              const isActive = activeFilters.includes(order.id);
              const isCancelled = order.status === 'cancelled';
              
              return (
                <button
                  key={order.id}
                  className={`filter-tag ${isActive && !isCancelled ? 'active' : ''} ${isCancelled ? 'cancelled' : ''}`}
                  onClick={() => toggleFilter(order.id)}
                >
                  {isActive && !isCancelled && <Check size={16} />}
                  {isCancelled && <X size={16} />}
                  {order.id}
                </button>
              );
            })}
          </div>

          {/* Order List */}
          <div className="order-list">
            {filteredOrders.map((order) => (
              <div key={order.id} className="order-card">
                {/* Order Header */}
                <div className="order-header">
                  <div className="order-info">
                    <h3>Order {order.id}</h3>
                    <p className="order-date">{order.date}</p>
                  </div>
                  <div className={`user-badge ${order.userColor}`}>
                    {order.userInitial}
                  </div>
                </div>

                {/* Order Items */}
                {order.items.length > 0 && (
                  <div className="order-items">
                    {order.items.map((item, idx) => (
                      <div key={idx} className="order-item">
                        <img 
                          src={item.image} 
                          alt={item.name}
                          className="item-image"
                        />
                        <div className="item-details">
                          <div className="item-name">{item.name}</div>
                          <div className="item-description">{item.description}</div>
                          <div className="item-price-row">
                            <span className="item-price">{item.price}</span>
                            <span className="item-qty">Qty: {item.qty}</span>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}

                {/* Order Footer */}
                <div className="order-footer">
                  <span className="total-items">X {order.totalItems} items</span>
                  <span className="total-price">{order.total}</span>
                </div>

                {/* Action Buttons */}
                <div className="order-actions">
                  {order.status === 'completed' && (
                    <button className="action-button complete">
                      <Check size={18} />
                      Completed
                    </button>
                  )}
                  {order.status === 'pending' && (
                    <>
                      <button className="action-button accept">
                        <Check size={18} />
                      </button>
                      <button className="action-button reject">
                        <X size={18} />
                      </button>
                    </>
                  )}
                  {order.status === 'preparing' && (
                    <button className="action-button preparing">
                      Preparing...
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}

export default AdminHome;
